struct modfind_ctx {
    const char *modname;
    flux_moderr_f *cb;
    void *arg;
};

char *flux_modname (const char *path, flux_moderr_f *cb, void *arg)
{
    void *dso;
    const char **np;
    char *cpy = NULL;
    int saved_errno;

    if (!path) {
        errno = EINVAL;
        return NULL;
    }
    if (!(dso = dlopen (path, RTLD_LAZY | RTLD_LOCAL | FLUX_DEEPBIND))) {
        if (cb)
            cb (dlerror (), arg);
        errno = ENOENT;
        return NULL;
    }
    dlerror ();
    if (!(np = dlsym (dso, "mod_name"))) {
        char *errmsg;
        if (cb && (errmsg = dlerror ()))
            cb (errmsg, arg);
        errno = EINVAL;
        goto error;
    }
    if (!*np) {
        errno = EINVAL;
        goto error;
    }
    if (!(cpy = strdup (*np)))
        goto error;
    dlclose (dso);
    return cpy;
error:
    saved_errno = errno;
    dlclose (dso);
    errno = saved_errno;
    return NULL;
}

/* dirwalk_filter_f callback for dirwalk_find()
 * This function should return 1 on match, 0 on no match.
 * dirwalk_find() will stop on first match since its count parameter is 1.
 */
static int mod_find_f (dirwalk_t *d, void *arg)
{
    struct modfind_ctx *ctx = arg;
    const char *path = dirwalk_path (d);
    char *name;
    int rc = 0;

    if ((name = flux_modname (path, ctx->cb, ctx->arg))) {
        if (!strcmp (name, ctx->modname))
            rc = 1;
        free (name);
    }
    return rc;
}

char *flux_modfind (const char *searchpath, const char *modname,
                    flux_moderr_f *cb, void *arg)
{
    char *result = NULL;
    zlist_t *l;
    struct modfind_ctx ctx;

    if (!searchpath || !modname) {
        errno = EINVAL;
        return NULL;
    }
    ctx.modname = modname;
    ctx.cb = cb;
    ctx.arg = arg;

    l = dirwalk_find (searchpath, 0, "*.so", 1, mod_find_f, &ctx);
    if (l) {
        result = zlist_pop (l);
        zlist_destroy (&l);
    }
    if (!result)
        errno = ENOENT;
    return result;
}

bool flux_module_debug_test (flux_t *h, int flag, bool clear)
{
    int *flagsp = flux_aux_get (h, "flux::debug_flags");

    if (!flagsp || !(*flagsp & flag))
        return false;
    if (clear)
        *flagsp &= ~flag;
    return true;
}

int flux_module_set_running (flux_t *h)
{
    flux_msg_t *msg;
    int rc = -1;

    if (!h) {
        errno = EINVAL;
        return -1;
    }
    if (!(msg = flux_keepalive_encode (0, FLUX_MODSTATE_RUNNING)))
        return -1;
    if (flux_send (h, msg, 0) < 0)
        goto done;
    rc = 0;
done:
    flux_msg_decref (msg);
    return rc;
}

