/* function prototype for invalid args testing below */
static int foo (flux_plugin_t *p,
                const char *topic,
                flux_plugin_arg_t *args,
                void *data)
{
    const char **sp = data;
    return flux_plugin_arg_pack (args,
                                 FLUX_PLUGIN_ARG_OUT,
                                 "{s:s s:s}",
                                 "fn", "foo",
                                 "data", *sp);
}

static int bar (flux_plugin_t *p,
                const char *topic,
                flux_plugin_arg_t *args,
                void *data)
{
    const char **sp = data;
    return flux_plugin_arg_pack (args,
                                 FLUX_PLUGIN_ARG_OUT,
                                 "{s:s s:s}",
                                 "fn", "bar",
                                 "data", *sp);
}

static char *foodata = "this is foo";
static char *bardata = "this is bar";

static const struct flux_plugin_handler tab[] = {
    { "foo.*", foo,  &foodata },
    { "*",     bar,  &bardata },
    { NULL,    NULL, NULL }
};

