    VALID_RANGE(1, LONG_TIMEOUT), DEFAULT(SLAVE_NET_TIMEOUT), BLOCK_SIZE(1),
    &PLock_slave_net_timeout, NOT_IN_BINLOG, ON_CHECK(0),
    ON_UPDATE(fix_slave_net_timeout));

