  char namebuf[64];  /* MAXTHREADNAMESIZE */
  int err;

  strncpy(namebuf, name, sizeof(namebuf) - 1);
  namebuf[sizeof(namebuf) - 1] = '\0';

  err = pthread_setname_np(namebuf);
  if (err)
