int qsw_init(void)
{
    assert(elanconf == NULL);

    elanconf = elanhost_config_create();

    if (elanhost_config_read(elanconf, NULL) < 0) {
        err("%p: error: %s\n", elanhost_config_err(elanconf));
        return -1;
    }

    return 0;
}

